class Point():
    def __init__(self, x, y):
        self.x = x
        self.y = y

    def __setattr__(self, name, value):
        print(f'set attr {name} to float({value})')
        self.__dict__[name] = float(value)

    def __repr__(self):
        return "Point(x=%r, y=%r)" % (self.x, self.y)
